# CASE only
SELECT upper(VALUEOF(NQ_SESSION.USER)) AS sv_USER,
   CASE WHEN ("Fact_META_LANGUAGE"."VARNAME" = 'vAtr_AnzUJ') THEN "Fact_META_LANGUAGE"."TEXT" END              AS vAtr_AnzUJ,
   CASE WHEN ("Fact_META_LANGUAGE"."VARNAME" = 'vAtr_DruckZeit') THEN "Fact_META_LANGUAGE"."TEXT" END          AS vAtr_DruckZeit,
   CASE WHEN ("Fact_META_LANGUAGE"."VARNAME" = 'vAtr_Formular') THEN "Fact_META_LANGUAGE"."TEXT" END           AS vAtr_Formular,
FROM
   RIS_Translation
WHERE
       "Fact_META_LANGUAGE"."REPORT_ID" = 308
   AND "RIS_Translation"."Fact_META_LANGUAGE"."SPRACHE" = CASE upper(:pv_LANG) WHEN upper('Fran?ais') THEN 'FR' WHEN upper('Italiano') THEN 'IT' ELSE 'DE' END


# MAX(CASE...
SELECT
   upper(VALUEOF(NQ_SESSION.USER))                                                                                  AS sv_USER,
   max(CASE WHEN ("Fact_META_LANGUAGE"."VARNAME" = 'vAtr_AnzUJ') THEN "Fact_META_LANGUAGE"."TEXT" END)              AS vAtr_AnzUJ,
   max(CASE WHEN ("Fact_META_LANGUAGE"."VARNAME" = 'vAtr_DruckZeit') THEN "Fact_META_LANGUAGE"."TEXT" END)          AS vAtr_DruckZeit,
   max(CASE WHEN ("Fact_META_LANGUAGE"."VARNAME" = 'vAtr_Formular') THEN "Fact_META_LANGUAGE"."TEXT" END)           AS vAtr_Formular,
FROM
   RIS_Translation
WHERE
       "Fact_META_LANGUAGE"."REPORT_ID" = 308
   AND "RIS_Translation"."Fact_META_LANGUAGE"."SPRACHE" = CASE upper(:pv_LANG) WHEN upper('Fran?ais') THEN 'FR' WHEN upper('Italiano') THEN 'IT' ELSE 'DE' END
